# 获取登录code
def get_access_token(location):
    code_pattern = re.compile("(?<=access=).*?(?=&)")
    return code_pattern.findall(location)[0]


# 虚拟ip地址
def fake_ip():
    return f"{random.randint(1, 255)}.{random.randint(1, 255)}.{random.randint(1, 255)}.{random.randint(1, 255)}"


# 登录
def login(user, password):
    if ("+86" in user) or "@" in user:
        user = user
    else:
        user = "+86" + user
    url1 = "https://api-user.huami.com/registrations/" + user + "/tokens"
    login_headers = {
        "Content-Type": "application/x-www-form-urlencoded;charset=UTF-8",
        "User-Agent": "Mozilla/5.0 (iPhone; CPU iPhone OS 14_7_1 like Mac OS X) AppleWebKit/605.1.15 (KHTML, like Gecko) Version/14.1.2",
        "X-Forwarded-For": fake_ip_addr
    }
    data1 = {
        "client_id": "HuaMi",
        "password": f"{password}",
        "redirect_uri": "https://s3-us-west-2.amazonaws.com/hm-registration/successsignin.html",
        "token": "access"
    }
    r1 = requests.post(url1, data=data1, headers=login_headers, allow_redirects=False)
    if r1.status_code != 303:
        print("登录异常，status: %d" % r1.status_code)
        return 0, 0
    location = r1.headers["Location"]
    try:
        code = get_access_token(location)
    except:
        return 0, 0
    # print("access_code获取成功！")
    # print(code)

    url2 = "https://account.huami.com/v2/client/login"
    if "+86" in user:
        data2 = {
            "app_name": "com.xiaomi.hm.health",
            "app_version": "4.6.0",
            "code": f"{code}",
            "country_code": "CN",
            "device_id": "2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1",
            "device_model": "phone",
            "grant_type": "access_token",
            "third_name": "huami_phone",
        }
    else:
        data2 = {
            "allow_registration=": "false",
            "app_name": "com.xiaomi.hm.health",
            "app_version": "6.3.5",
            "code": f"{code}",
            "country_code": "CN",
            "device_id": "2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1",
            "device_model": "phone",
            "dn": "api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com",
            "grant_type": "access_token",
            "lang": "zh_CN",
            "os_version": "1.5.0",
            "source": "com.xiaomi.hm.health",
            "third_name": "email",
        }
    r2 = requests.post(url2, data=data2, headers=login_headers).json()
    login_token = r2["token_info"]["login_token"]
    # print("login_token获取成功！")
    # print(login_token)
    userid = r2["token_info"]["user_id"]
    # print("userid获取成功！")
    # print(userid)

    return login_token, userid


def desensitize_user_name(user):
    return f'{user[:3]}****{user[-4:]}'


